replace into gossip_menu (entry, text_id, condition_id) VALUES (@gossip_menu_id, @magic_number, '0'); 
replace into broadcast_text (entry, Male_Text) values (@magic_number, 'My son likes to exaggerate, but he is a hard worker. If you\'re looking to be a skilled blacksmith, you need to work hard at it to succeed.');
replace into npc_text (ID, BroadcastTextID0) values (@magic_number, @magic_number);
replace into gossip_menu (entry, text_id, condition_id) VALUES (@gossip_menu_id, @magic_number, '0'); 
replace into broadcast_text (entry, Male_Text) values (@magic_number, 'My father has been teaching me how to craft and repair armor for pretty much all of my life. If you\'re ever wanting to learn a thing or to, you should speak with him, he certainly is the best.');
